import React from 'react';

const Navbar = ({ setCurrentPage }) => {
    const handleNavigation = (page) => {
        setCurrentPage(page);
    };

    return (
        <nav style={{
            position: 'fixed', 
            top: 0,
            left: 0,
            width: '100%',
            height: '80px', // Set a fixed height
            display: 'flex',
            justifyContent: 'space-around',
            alignItems: 'center',
            backgroundColor: 'white', 
            boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)', // Optional shadow for visibility
            zIndex: 1000
        }}>
            <div onClick={() => handleNavigation('calendar')} style={navItemStyle}>WRITE</div>
            <div onClick={() => handleNavigation('viewdata')} style={navItemStyle}>ENTRIES</div>
            <div onClick={() => handleNavigation('cards')} style={navItemStyle}>TIMELINE</div>
            <div onClick={() => handleNavigation('profile')} style={navItemStyle}>PROFILE</div>
        </nav>
    );
};

const navItemStyle = {
    padding: '10px',
    borderRadius: '5px',
    backgroundColor: '#4CAF50',
    height: '60px',
    width: '250px',
    color: '#fff',
    cursor: 'pointer',
    fontFamily: 'Dancing Script, cursive',
    fontSize: '24px', // Increase font size
    display: 'flex', // Center-align with flexbox
    alignItems: 'center', // Center vertically
    justifyContent: 'center' // Center horizontally
};

export default Navbar;
