import React, { useState } from 'react';
import CalendarPage from './components/CalendarPage';  // Import CalendarPage
import AddDataPage from './components/AddDataPage';    // Import AddDataPage
import ViewDataPage from './components/ViewDataPage';  // Import ViewDataPage
import './App.css';  // Include your styles

const App = () => {
    const [currentPage, setCurrentPage] = useState('calendar');
    const [selectedDate, setSelectedDate] = useState(null);
    const [uploads, setUploads] = useState({});

    const handleAddData = () => {
        if (selectedDate) {
            setCurrentPage('addData');
        }
    };

    const handleViewData = () => {
        setCurrentPage('viewData');
    };

    const handleUpload = (date, newUpload) => {
        setUploads(prevUploads => ({
            ...prevUploads,
            [date]: newUpload
        }));
        setCurrentPage('calendar'); // Return to calendar after upload
    };

    return (
      <div>
        <div className="app">
            {currentPage === 'calendar' && (
                <CalendarPage 
                    setCurrentPage={setCurrentPage} 
                    setSelectedDate={setSelectedDate} 
                    selectedDate={selectedDate} 
                    handleAddData={handleAddData} 
                    handleViewData={handleViewData} 
                />
            )}
            {currentPage === 'addData' && (
                <AddDataPage 
                    selectedDate={selectedDate} 
                    handleUpload={handleUpload} 
                    setCurrentPage={setCurrentPage} 
                />
            )}
            {currentPage === 'viewData' && (
                <ViewDataPage 
                    uploads={uploads} 
                    setCurrentPage={setCurrentPage} 
                />
            )}
        </div>
        </div>
    );
};

// Export the App component
export default App;
