import React, { useState, useEffect } from 'react';
import "./CalendarPage.css";
import Diary from "../assets/opendiary2.png";

const CalendarPage = ({ setCurrentPage, handleUpload }) => {
    const [selectedDate, setSelectedDate] = useState(new Date()); // Initialize with current date
    const [textValue, setTextValue] = useState(`Selected Date: ${new Date().toDateString()}`);
    const [isAddingData, setIsAddingData] = useState(false); // State to toggle between calendar and add data form
    const [data, setData] = useState(''); // State to hold form input

    // Function to handle date change
    const handleDateChange = (event) => {
        const newDate = new Date(event.target.value);
        setSelectedDate(newDate);
        setTextValue(`Selected Date: ${newDate.toDateString()}`);
    };

    // Effect to reset selectedDate if it's null or invalid
    useEffect(() => {
        if (!selectedDate || isNaN(selectedDate.getTime())) {
            setSelectedDate(new Date()); // Set to current date if no valid date is selected
        }
    }, [selectedDate]);

    // Handle input change
    const handleDataChange = (event) => {
        setData(event.target.value);
    };

    // Handle form submission
    const handleSubmit = (event) => {
        event.preventDefault();
        if (selectedDate) {
            handleUpload(selectedDate, data); // Call the handleUpload function passed via props
            setIsAddingData(false); // Hide the add data form
        } else {
            alert('Please select a date before adding data.');
        }
    };

    return (
        <div style={{ padding: '20px', marginTop: '2vh' }}>
            <div
                style={{
                    width: '93%',
                    height: '60px',
                    display: 'flex',
                    marginLeft: '50px',
                    justifyContent: 'center',
                    alignItems: 'center',
                }}
            >
                <label style={{ marginRight: '10px' }}>Select a Date:</label>
                <input
                    type="date"
                    value={selectedDate.toISOString().substr(0, 10)}
                    onChange={handleDateChange}
                    style={{ padding: '5px', borderRadius: '5px', border: '1px solid #ccc' }}
                />
            </div>
            <div
                style={{
                    width: '803px',
                    height: '552.5px',
                    margin: 'auto',
                    padding: '20px',
                    backgroundImage: `url(${Diary})`,
                    backgroundPosition: 'center',
                    backgroundSize: 'cover'
                }}
            >
                {!isAddingData ? (
                    <>
                        {/* Container for Left and Right Divs */}
                        <div style={{ display: 'flex', justifyContent: 'space-between' }}>
                            {/* Left Div for Random Images */}
                            <div
                                style={{
                                    width: '44%',
                                    height: '510px',
                                    marginLeft: '50px',
                                    backgroundColor: '#f0f0f0',
                                    padding: '10px',
                                }}
                            >
                                <h2>Random Images</h2>
                                <img src="https://source.unsplash.com/random/200x200" alt="Random 1" style={{ width: '100%', marginBottom: '10px' }} />
                                <img src="https://source.unsplash.com/random/201x201" alt="Random 2" style={{ width: '100%', marginBottom: '10px' }} />
                                <img src="https://source.unsplash.com/random/202x202" alt="Random 3" style={{ width: '100%', marginBottom: '10px' }} />
                            </div>

                            {/* Right Div for Text Area */}
                            <div
                                style={{
                                    width: '47%',
                                    height: '500px',
                                    marginTop: '5px',
                                    marginRight: '5px',
                                    backgroundColor: 'transparent',
                                    padding: '10px',
                                }}
                            >
                                <textarea
                                    style={{
                                        height: '100%',
                                        width: '100%',
                                        padding: '10px',
                                        border: 'none',
                                        outline: 'none',
                                        resize: 'none',
                                        boxSizing: 'border-box',
                                        backgroundColor: 'transparent',
                                    }}
                                    value={textValue}
                                    onChange={(e) => setTextValue(e.target.value)} // Make textarea editable
                                />
                            </div>
                        </div>

                        {/* Show buttons only if a date is selected */}
                        {selectedDate && (
                            <div className="button-group" style={{ textAlign: 'center', marginTop: '20px' }}>
                                <button onClick={() => setIsAddingData(true)} style={{ marginRight: '10px' }}>Add Data</button>
                                <button onClick={() => setCurrentPage('viewData')}>View Data</button>
                            </div>
                        )}
                    </>
                ) : (
                    <div style={{ width: '100%', maxWidth: '800px', margin: 'auto' }}>
                        <h1>Add Data</h1>
                        <form onSubmit={handleSubmit} style={{ marginTop: '20px' }}>
                            <div style={{ marginBottom: '20px' }}>
                                <label htmlFor="dataInput" style={{ display: 'block', marginBottom: '10px' }}>
                                    Data for {selectedDate ? selectedDate.toDateString() : 'Selected Date'}
                                </label>
                                <textarea
                                    id="dataInput"
                                    value={data}
                                    onChange={handleDataChange}
                                    rows="5"
                                    style={{
                                        width: '100%',
                                        padding: '10px',
                                        borderRadius: '5px',
                                        border: '1px solid #ccc',
                                        boxSizing: 'border-box'
                                    }}
                                />
                            </div>
                            <button type="submit" style={{ padding: '10px 20px', borderRadius: '5px', border: 'none', backgroundColor: '#4CAF50', color: '#fff', cursor: 'pointer' }}>
                                Submit
                            </button>
                            <button
                                type="button"
                                onClick={() => setIsAddingData(false)}
                                style={{ padding: '10px 20px', borderRadius: '5px', border: 'none', backgroundColor: '#f44336', color: '#fff', cursor: 'pointer', marginLeft: '10px' }}
                            >
                                Cancel
                            </button>
                        </form>
                    </div>
                )}
            </div>
        </div>
    );
};

export default CalendarPage;
