import React, { useState, useEffect } from "react";
import axios from "axios";
import InfiniteScroll from "react-infinite-scroller";

const ViewData = () => {
  const [pokemon, setPokemon] = useState([]);
  const [url, setUrl] = useState("https://pokeapi.co/api/v2/pokemon/?limit=200");
  const [dateList,setDateList] = useState([]);
  // Generate consecutive dates starting from today and moving backwards
  useEffect(() => {
    const today = new Date();
    const dates = Array.from({ length: 10 }, (_, i) => {
      const date = new Date(today);
      date.setDate(today.getDate() - i); // Go backwards by 'i' days from today
      return date.toISOString().substr(0, 10);
    });
    setDateList(dates);
    console.log("Date List:", dates); // Debugging: log dateList
  }, []);

  const loadPokemon = () => {
    axios
      .get(url)
      .then(res => {
        setPokemon(prevPokemon => [...prevPokemon, ...res.data.results]);
        setUrl(res.data.next);
      })
      .catch(error => {
        console.log(error);
      });
  };

  return (
    <div style={{ width: "90%", margin: "0 auto", paddingTop: "20px" }}>
      <InfiniteScroll
        pageStart={0}
        loadMore={loadPokemon}
        hasMore={!!url}
        loader={
          <div style={{ textAlign: "center", margin: "20px 0" }} key={0}>
            Loading ...
          </div>
        }
      >
        <div style={{ display: "flex", flexWrap: "wrap", justifyContent: "center" }}>
          {pokemon.map((poke, i) => (
            <div
              key={poke.name + i}
              style={{
                border: "1px solid #ddd",
                borderRadius: "10px",
                width: "200px",
                margin: "10px",
                textAlign: "center",
                boxShadow: "0 4px 8px rgba(0, 0, 0, 0.1)"
              }}
            >
              <img
                src={`https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/${i + 1}.png`}
                alt={poke.name}
                style={{
                  width: "100%",
                  borderTopLeftRadius: "10px",
                  borderTopRightRadius: "10px",
                  backgroundColor: "#f9f9f9",
                  padding: "10px"
                }}
              />
              <div style={{ padding: "10px" }}>
                <h5 style={{ margin: "10px 0", fontSize: "1.1em" }}>{poke.name}</h5>
                <a
                  href={poke.url}
                  target="_blank"
                  rel="noopener noreferrer"
                  style={{
                    color: "#007bff",
                    textDecoration: "none",
                    fontSize: "0.9em"
                  }}
                >
                  More details
                </a>
              </div>
            </div>
          ))}
        </div>
      </InfiniteScroll>
    </div>
  );
};

export default ViewData;
