import React, { useState } from 'react';
import Calendar from "react-calendar";
import "react-calendar/dist/Calendar.css";
import "./CalendarPage.css";

const CalendarPage = (setCurrentPage) => {
    const [selectedDate, setSelectedDate] = useState(null);  // State to hold selected date

    // Function to handle date click
    const Date_Click_Fun = (date) => {
        setSelectedDate(date);
    };


    return (
        <div className="calendar-page">
            <h1>Select a Date from the Calendar</h1>
            <div className="calendar-container">
                <Calendar className="black"
                    onClickDay={Date_Click_Fun}
                    value={selectedDate}  // Show selected date on the calendar
                />
            </div>
            {/* Show buttons only if a date is selected */}
            {selectedDate && (
                <div className="button-group">
                    <button onClick={setCurrentPage='addData'}>Add Data</button>
                    <button onClick={setCurrentPage='viewData'}>View Data</button>
                </div>
            )}
        </div>
    );
};

export default CalendarPage;
