import React, { useState, useEffect, useRef } from 'react';
import Diary from "../assets/opendiary2.png";

const AddData = ({ setCurrentPage, handleUpload }) => {
    const [selectedDate] = useState(new Date()); // Initialize with current date
    const [textValue] = useState(new Date().toLocaleDateString(undefined, {
        day: '2-digit',
        month: 'short',
        year: 'numeric'
    }));
    const [data, setData] = useState(''); // State to hold form input
    const [images, setImages] = useState([]); // State to hold uploaded images

    // Handle input change
    const handleDataChange = (event) => {
        setData(event.target.value);
    };

    // Handle form submission
    const handleSubmit = (event) => {
        event.preventDefault();
        if (data.trim() !== '') {
            handleUpload(selectedDate, data); // Call the handleUpload function passed via props
            setData(''); // Clear the text area after submission
            // Optionally: reset other states if needed
        } else {
            alert('Please enter data before adding.');
        }
    };

    // Handle image upload
    const handleImageUpload = (event) => {
        const files = Array.from(event.target.files);
        const newImages = files.map(file => URL.createObjectURL(file));
        setImages(prevImages => [...prevImages, ...newImages]);
    };

    const imageinp = useRef(null);

    const handleAddImage = () => {
        imageinp.current.click();
    }

    return (
        <div style={{ padding: '20px', marginTop: '1vh' }}>
            <div
                style={{
                    width: '93%',
                    height: '60px',
                    display: 'flex',
                    marginLeft: '50px',
                    justifyContent: 'center',
                    alignItems: 'center',
                }}
            >
                {/* Display current date only */}
            </div>
            <div
                style={{
                    width: '803px',
                    height: '552.5px',
                    margin: 'auto',
                    padding: '20px',
                    backgroundImage: `url(${Diary})`,
                    backgroundPosition: 'center',
                    backgroundSize: 'cover'
                }}
            >
                {/* Container for Left and Right Divs */}
                <div style={{ display: 'flex', justifyContent: 'space-between' }}>
                    {/* Left Div for Random Images */}
                    <div
                        style={{
                            width: '44%',
                            height: '510px',
                            marginLeft: '50px',
                            backgroundColor: 'transparent',
                            padding: '10px',
                            overflowY: 'auto', // Enable scrolling if content overflows
                        }}
                    >
                        <input
                            type="file"
                            accept="image/*"
                            ref={imageinp}
                            multiple
                            onChange={handleImageUpload}
                            style={{ display: 'none' }}
                        />
                        {images.map((image, index) => (
                            <img key={index} src={image} alt={`Uploaded ${index}`} style={{ width: '100%', marginBottom: '10px' }} />
                        ))}
                    </div>

                    {/* Right Div for Text Area */}
                    <div
                        style={{
                            width: '47%',
                            height: '500px',
                            marginTop: '5px',
                            marginRight: '5px',
                            backgroundColor: 'transparent',
                            padding: '10px',
                        }}
                    >
                        <span style={{ marginRight: '10px',color:'black'}}>{textValue}</span>
                        <textarea
                            style={{
                                height: '100%',
                                width: '100%',
                                padding: '10px',
                                border: 'none',
                                outline: 'none',
                                resize: 'none',
                                boxSizing: 'border-box',
                                backgroundColor: 'transparent',fontFamily: 'Dancing Script, cursive'
                            }}
                            value={data}
                            onChange={handleDataChange}
                        />
                    </div>
                </div>
                <div style={{ display: 'flex', justifyContent: 'center', marginTop: '20px' }}>
                    <button
                        onClick={handleAddImage}
                        style={{
                            width: '40%',
                            padding: '10px',
                            borderRadius: '5px',
                            border: 'none',
                            backgroundColor: '#4CAF50',
                            color: '#fff',
                            cursor: 'pointer',
                            marginRight: '10px', // Add margin between buttons
                        }}
                    >
                        Add Images
                    </button>
                    <button
                        onClick={handleSubmit}
                        style={{
                            width: '40%',
                            padding: '10px',
                            borderRadius: '5px',
                            border: 'none',
                            backgroundColor: '#4CAF50',
                            color: '#fff',
                            cursor: 'pointer',
                        }}
                    >
                        Submit Data
                    </button>
                </div>
            </div>
        </div>
    );
};

export default AddData;
