import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import Diary from "../assets/opendiary2.png"; // Your background image

const Profile = () => {
    const [profile, setProfile] = useState({
        name: '',
        email: '',
        profilePicture: 'https://via.placeholder.com/150', // Placeholder image
    });
    const [newName, setNewName] = useState('');
    const [newEmail, setNewEmail] = useState('');
    const imageInput = useRef(null);
    const [image, setImage] = useState(null); // State for the profile image

    // Fetch user details from AWS Cognito
    const fetchProfile = async () => {
        const email = localStorage.getItem('email'); // Get email from localStorage
        try {
            const response = await axios.get(`http://localhost:5000/api/auth/getUserAttributes`, {
                params: { email }
            });
            const { name, email: userEmail } = response.data;
            setProfile({ name, email: userEmail, profilePicture: profile.profilePicture });
            setNewName(name);
            setNewEmail(userEmail);
        } catch (error) {
            console.error('Error fetching profile:', error);
        }
    };

    // Handle profile picture upload
    const handleImageUpload = (event) => {
        const file = event.target.files[0];
        if (file) {
            setImage(file); // Update the image state with the file
        }
    };

    // Handle profile update
    const handleUpdateProfile = async () => {
        const email = localStorage.getItem('email'); // Get email from localStorage
        try {
            // Update the user's name and email
            await axios.post('http://localhost:5000/api/auth/updateUserAttributes', {
                email,
                attributes: {
                    name: newName,
                    email: newEmail,
                }
            });

            // Upload the new profile picture if it exists
            if (image) {
                const formData = new FormData();
                formData.append('image', image);
                const uploadResponse = await axios.post('http://localhost:5000/api/diary/upload-image', formData, {
                    headers: { 'Content-Type': 'multipart/form-data' }
                });
                const imageUrl = uploadResponse.data.imageUrl;
                // Assuming you want to update the profile picture URL here
                setProfile({ ...profile, profilePicture: imageUrl });
            }

            alert('Profile updated successfully!');
        } catch (error) {
            console.error('Error updating profile:', error);
        }
    };

    // Fetch profile data on component mount
    useEffect(() => {
        fetchProfile();
    }, []);

    return (
        <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center', marginTop: '50px', backgroundImage: `url(${Diary})`, padding: '20px' }}>
            {/* Profile Picture */}
            <img
                src={profile.profilePicture}
                alt="Profile"
                style={{ borderRadius: '50%', width: '150px', height: '150px', marginBottom: '20px' }}
            />

            {/* User Name */}
            <h1 style={{ fontSize: '36px', color: '#4CAF50' }}>
                <span>{profile.name}</span>
                <button onClick={() => setNewName(profile.name)} style={{ marginLeft: '10px' }}>Edit</button>
            </h1>

            {/* Email */}
            <p style={{ fontSize: '24px', color: '#555' }}>
                <span>{profile.email}</span>
                <button onClick={() => setNewEmail(profile.email)} style={{ marginLeft: '10px' }}>Edit</button>
            </p>

            {/* Input Fields for New Name and Email */}
            <input
                type="text"
                value={newName}
                onChange={(e) => setNewName(e.target.value)}
                placeholder="New Name"
                style={{ margin: '10px 0', padding: '10px', width: '200px', borderRadius: '5px', border: '1px solid #ccc' }}
            />
            <input
                type="email"
                value={newEmail}
                onChange={(e) => setNewEmail(e.target.value)}
                placeholder="New Email"
                style={{ margin: '10px 0', padding: '10px', width: '200px', borderRadius: '5px', border: '1px solid #ccc' }}
            />
            
            {/* Upload Profile Picture */}
            <input
                type="file"
                accept="image/*"
                ref={imageInput}
                onChange={handleImageUpload}
                style={{ display: 'none' }} // Hide file input
            />
            <button onClick={() => imageInput.current.click()} style={{ margin: '10px 0', padding: '10px', borderRadius: '5px', border: 'none', backgroundColor: '#4CAF50', color: '#fff' }}>
                Upload Profile Picture
            </button>

            {/* Update Profile Button */}
            <button
                onClick={handleUpdateProfile}
                style={{
                    padding: '10px 20px',
                    borderRadius: '5px',
                    border: 'none',
                    backgroundColor: '#4CAF50',
                    color: '#fff',
                    cursor: 'pointer',
                    marginTop: '20px',
                    fontSize: '18px',
                }}
            >
                Update Profile
            </button>
        </div>
    );
};

export default Profile;
