import React from 'react';
import { useTheme } from './ThemeContext'; // Import the custom hook

const ProfilePage = () => {
  // Access global theme context
  const { backgroundColor, fontFamily, changeBackgroundColor, changeFontFamily } = useTheme();

  return (
    <div
      style={{
        backgroundColor: backgroundColor,
        fontFamily: fontFamily,
        padding: '20px',
        height: '100vh',
        transition: 'all 0.3s ease',
      }}
    >
      <h1>Profile Page</h1>
      <div>
        <h2>Name: John Doe</h2>
        <p>Email: johndoe@example.com</p>
      </div>

      <div>
        <h3>Change Background Color</h3>
        <button onClick={() => changeBackgroundColor("#f0f8ff")}>Light Blue</button>
        <button onClick={() => changeBackgroundColor("#ffebcd")}>Blanched Almond</button>
        <button onClick={() => changeBackgroundColor("#add8e6")}>Light Sky Blue</button>
        <button onClick={() => changeBackgroundColor("#ffffff")}>White</button>
      </div>

      <div>
        <h3>Change Font Family</h3>
        <button onClick={() => changeFontFamily("Arial, sans-serif")}>Arial</button>
        <button onClick={() => changeFontFamily("Georgia, serif")}>Georgia</button>
        <button onClick={() => changeFontFamily("Courier New, monospace")}>Courier New</button>
        <button onClick={() => changeFontFamily("Comic Sans MS, cursive")}>Comic Sans</button>
      </div>
    </div>
  );
};

export default ProfilePage;
