import React, { useState, useEffect } from 'react';
import { useLocation } from "react-router-dom";
import axios from 'axios';
import Diary from "../assets/opendiary2.png";

const ViewData = ({ selectedDate }) => {
    const [textValue, setTextValue] = useState('');
    const [imageUrl, setImageUrl] = useState('');
    const email = localStorage.getItem('email');

    const fetchDataByDateAndEmail = async (entryDate, email) => {
        try {
            const response = await axios.get(`http://localhost:5000/api/diary/get-entry`, {
                params: { entryDate, email }
            });
            const { content, imageUrl } = response.data;
            setTextValue(content || 'No content available for this date');
            setImageUrl(imageUrl || '');
        } catch (error) {
            console.error('Error fetching data:', error);
            setTextValue('Error fetching data');
            setImageUrl('');
        }
    };

    useEffect(() => {
        fetchDataByDateAndEmail(selectedDate, email);
    }, [selectedDate, email]);

    return (
        <div style={{ padding: '20px', paddingTop: '100px' }}>
            <div style={{
                width: '93%',
                height: '60px',
                display: 'flex',
                marginLeft: '50px',
                justifyContent: 'center',
                alignItems: 'center',
            }}>
                <label style={{ color: 'white', marginRight: '10px' }}>Selected Date:</label>
                <input
                    type="date"
                    value={selectedDate}
                    style={{ padding: '5px', borderRadius: '5px', border: '1px solid #ccc' }}
                    readOnly
                />
            </div>

            <div style={{
                width: '803px',
                height: '552.5px',
                margin: 'auto',
                padding: '20px',
                backgroundImage: `url(${Diary})`,
                backgroundPosition: 'center',
                backgroundSize: 'cover',
            }}>
                <div style={{ display: 'flex', justifyContent: 'space-between' }}>
                    <div style={{
                        width: '44%',
                        height: '510px',
                        marginLeft: '50px',
                        backgroundColor: 'transparent',
                        padding: '10px',
                        overflowY: 'auto',
                    }}>
                        {imageUrl ? (
                            <img src={imageUrl} alt="Diary entry" style={{ width: '100%', marginBottom: '10px' }} />
                        ) : (
                            <p>No image available for this date</p>
                        )}
                    </div>

                    <div style={{
                        width: '47%',
                        height: '500px',
                        marginRight: '20px',
                        backgroundColor: 'transparent',
                        padding: '10px',
                        overflowY: 'auto',
                        whiteSpace: 'pre-wrap',
                    }}>
                        {textValue}
                    </div>
                </div>
            </div>
        </div>
    );
};

export default ViewData;
