import React, { useState, useEffect } from 'react';
import { CognitoIdentityProviderClient, GetUserCommand } from '@aws-sdk/client-cognito-identity-provider';
import { useNavigate } from 'react-router-dom';

const Profile = () => {
    const [profile, setProfile] = useState({ name: '', email: '', profilePicture: '' });
    const [isEditingName, setIsEditingName] = useState(false);
    const [isEditingEmail, setIsEditingEmail] = useState(false);
    const [newName, setNewName] = useState('');
    const [newEmail, setNewEmail] = useState('');
    const navigate = useNavigate();

    const client = new CognitoIdentityProviderClient({ region: 'your-region' }); // Replace 'your-region' with your AWS region

    // Fetch profile data from AWS Cognito
    useEffect(() => {
        const fetchProfile = async () => {
            try {
                const command = new GetUserCommand({ AccessToken: localStorage.getItem('token') }); // Replace with your method of obtaining the access token
                const response = await client.send(command);
                
                const attributes = response.UserAttributes.reduce((acc, attr) => {
                    acc[attr.Name] = attr.Value;
                    return acc;
                }, {});

                setProfile({
                    name: attributes['name'] || '',
                    email: attributes['email'] || '',
                    profilePicture: 'https://via.placeholder.com/150', // Placeholder image
                });

                setNewName(attributes['name'] || '');
                setNewEmail(attributes['email'] || '');
            } catch (error) {
                console.error('Error fetching profile:', error);
            }
        };
        fetchProfile();
    }, []);

    // Handle profile update
    const handleUpdateProfile = async () => {
        // Implement your update logic here to save newName and newEmail to Cognito
        alert('Profile updated successfully!');
    };

    const handleBack = () => {
        navigate(-1); // Navigate to the previous page
    };

    return (
        <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center', marginTop: '50px' }}>
            <img
                src={profile.profilePicture}
                alt="Profile"
                style={{ borderRadius: '50%', width: '150px', height: '150px', marginBottom: '20px' }}
            />
            <h1 style={{ fontSize: '36px', color: '#4CAF50' }}>{profile.name}</h1>
            <p style={{ fontSize: '24px', color: '#555' }}>{profile.email}</p>

            {/* Display Name */}
            <div style={{ marginBottom: '10px' }}>
                <strong>Name:</strong> {isEditingName ? (
                    <input 
                        type="text" 
                        value={newName} 
                        onChange={(e) => setNewName(e.target.value)} 
                        placeholder="Enter new name" 
                    />
                ) : (
                    <span>{profile.name}</span>
                )}
                <button onClick={() => setIsEditingName(!isEditingName)}>
                    {isEditingName ? 'Save' : 'Edit'}
                </button>
            </div>

            {/* Display Email */}
            <div style={{ marginBottom: '10px' }}>
                <strong>Email:</strong> {isEditingEmail ? (
                    <input 
                        type="email" 
                        value={newEmail} 
                        onChange={(e) => setNewEmail(e.target.value)} 
                        placeholder="Enter new email" 
                    />
                ) : (
                    <span>{profile.email}</span>
                )}
                <button onClick={() => setIsEditingEmail(!isEditingEmail)}>
                    {isEditingEmail ? 'Save' : 'Edit'}
                </button>
            </div>

            <button onClick={handleUpdateProfile} style={{ padding: '10px', backgroundColor: '#4CAF50', color: 'white', margin: '20px 0' }}>
                Update Profile
            </button>

            <button onClick={handleBack} style={{ padding: '10px', backgroundColor: '#f44336', color: 'white' }}>
                Back
            </button>
        </div>
    );
};

export default Profile;
