import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom'; // Import the useNavigate hook
import ForgotPassword from './ForgotPassword'; // Import the ForgotPassword component
import Diary from './Diary.png'
export default function SignIn() {
  const [emailError, setEmailError] = useState(false);
  const [emailErrorMessage, setEmailErrorMessage] = useState('');
  const [passwordError, setPasswordError] = useState(false);
  const [passwordErrorMessage, setPasswordErrorMessage] = useState('');
  const [open, setOpen] = useState(false);
  const navigate = useNavigate(); // Initialize the useNavigate hook

  const handleClickOpen = () => {
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    const data = new FormData(event.currentTarget);
    console.log({
      email: data.get('email'),
      password: data.get('password'),
    });
  };

  const validateInputs = () => {
    const email = document.getElementById('email');
    const password = document.getElementById('password');

    let isValid = true;

    if (!email.value || !/\S+@\S+\.\S+/.test(email.value)) {
      setEmailError(true);
      setEmailErrorMessage('Please enter a valid email address.');
      isValid = false;
    } else {
      setEmailError(false);
      setEmailErrorMessage('');
    }

    if (!password.value || password.value.length < 6) {
      setPasswordError(true);
      setPasswordErrorMessage('Password must be at least 6 characters long.');
      isValid = false;
    } else {
      setPasswordError(false);
      setPasswordErrorMessage('');
    }

    return isValid;
  };

  const handleSignUpRedirect = () => {
    navigate('/sign-up'); // Navigate to the Sign Up page
  };

  return (
    <div style={{ padding: '20px', marginTop: '10vh', backgroundColor:'red' }}>
      <div style={{ width: '464px',height:'617px', margin: 'auto', padding: '20px', boxShadow: '0 5px 15px rgba(0, 0, 0, 0.1)', backgroundImage: `url(${Diary})`,backgroundPosition: 'center', backgroundSize: 'cover'}}>
        <h1 style={{ fontSize: '2rem', textAlign: 'center', marginTop:'40px'}}>Sign in</h1>
        <form onSubmit={handleSubmit} noValidate style={{ display: 'flex', flexDirection: 'column', gap: '16px', marginLeft:'20px'}}>
          <div style={{ display: 'flex', flexDirection: 'column' }}>
            <label htmlFor="email">Email</label>
            <input
              type="email"
              id="email"
              name="email"
              required
              placeholder="your@email.com"
              style={{ padding: '8px', borderColor: emailError ? 'red' : '#ccc' }}
            />
            {emailError && <span style={{ color: 'red' }}>{emailErrorMessage}</span>}
          </div>
          <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
            <label htmlFor="password">Password</label>
            <button type="button" onClick={handleClickOpen} style={{ background: 'none', border: 'none', color: 'blue', cursor: 'pointer' }}>
              Forgot your password?
            </button>
          </div>
          <input
            type="password"
            id="password"
            name="password"
            required
            placeholder="••••••"
            style={{ padding: '8px', borderColor: passwordError ? 'red' : '#ccc' }}
          />
          {passwordError && <span style={{ color: 'red' }}>{passwordErrorMessage}</span>}
          <div>
            <label>
              <input type="checkbox" name="remember" /> Remember me
            </label>
          </div>
          <button type="submit" onClick={validateInputs} style={{ padding: '10px', backgroundColor: 'blue', color: 'white', border: 'none', cursor: 'pointer' }}>
            Sign in
          </button>
        </form>
        <p style={{ textAlign: 'center' }}>
          Don&apos;t have an account? <a href="/sign-up" onClick={handleSignUpRedirect} style={{ color: 'blue' }}>Sign up</a>
        </p>
        <hr />
      </div>
      {open && <ForgotPassword open={open} handleClose={handleClose} />} {/* Render ForgotPassword component */}
    </div>
  );
}
