import React, { Component } from "react";
import ReactDOM from "react-dom";
import axios from "axios";
import InfiniteScroll from "react-infinite-scroller";

export default class App extends Component {
  state = {
    url: "https://pokeapi.co/api/v2/pokemon/?limit=200",
    pokemon: [],
    itemsCountPerPage: 20,
    activePage: 1
  };

  loadPokemon = () => {
    axios
      .get(this.state.url)
      .then(res => {
        this.setState(prevState => ({
          pokemon: [...prevState.pokemon, ...res.data.results],
          url: res.data.next
        }));
      })
      .catch(error => {
        console.log(error);
      });
  };

  render() {
    return (
      <div style={{ width: "90%", margin: "0 auto", paddingTop: "20px" }}>
        <InfiniteScroll
          pageStart={0}
          loadMore={this.loadPokemon}
          hasMore={this.state.url}
          loader={
            <div style={{ textAlign: "center", margin: "20px 0" }} key={0}>
              Loading ...
            </div>
          }
        >
          <div style={{ display: "flex", flexWrap: "wrap", justifyContent: "center" }}>
            {this.state.pokemon.map((pokemon, i) => (
              <div
                key={pokemon.name + i}
                style={{
                  border: "1px solid #ddd",
                  borderRadius: "10px",
                  width: "200px",
                  margin: "10px",
                  textAlign: "center",
                  boxShadow: "0 4px 8px rgba(0, 0, 0, 0.1)"
                }}
              >
                <img
                  src={`https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/${i + 1}.png`}
                  alt={pokemon.name}
                  style={{
                    width: "100%",
                    borderTopLeftRadius: "10px",
                    borderTopRightRadius: "10px",
                    backgroundColor: "#f9f9f9",
                    padding: "10px"
                  }}
                />
                <div style={{ padding: "10px" }}>
                  <h5 style={{ margin: "10px 0", fontSize: "1.1em" }}>{pokemon.name}</h5>
                  <a
                    href={pokemon.url}
                    target="_blank"
                    rel="noopener noreferrer"
                    style={{
                      color: "#007bff",
                      textDecoration: "none",
                      fontSize: "0.9em"
                    }}
                  >
                    More details
                  </a>
                </div>
              </div>
            ))}
          </div>
        </InfiniteScroll>
      </div>
    );
  }
}

const rootElement = document.getElementById("root");
ReactDOM.render(<App />, rootElement);
