import React, { useState, useEffect } from "react";
import InfiniteScroll from "react-infinite-scroller";
import axios from "axios";

const ViewData = () => {
  const [entries, setEntries] = useState([]); // State to hold fetched entries
  const [dateList, setDateList] = useState([]); // List of consecutive dates
  const [loading, setLoading] = useState(false); // State to handle loading
  const [hasMore, setHasMore] = useState(true); // Flag to check if more entries are available
  const email = localStorage.getItem("email");

  // Generate consecutive dates starting from today and moving backwards
  useEffect(() => {
    const today = new Date();
    const dates = Array.from({ length: 10 }, (_, i) => {
      const date = new Date(today);
      date.setDate(today.getDate() - i); // Go backwards by 'i' days from today
      return date.toISOString().substr(0, 10);
    });
    setDateList(dates);
    console.log("Date List:", dates); // Debugging: log dateList
  }, []);

  // Function to fetch data by date and email
  const fetchDataByDateAndEmail = async (entryDate, email) => {
    try {
      const response = await axios.get(`http://localhost:5000/api/diary/get-entry`, {
        params: { entryDate, email },
      });
      return {
        date: entryDate,
        imageUrl: response.data.imageUrl || null, // Make sure this matches your API response
      };
    } catch (error) {
      console.error(`Error fetching data for ${entryDate}:`, error);
      return { date: entryDate, imageUrl: null }; // Return null imageUrl on error
    }
  };

  // Load data for each date in dateList
  const loadEntries = async () => {
    if (loading || !hasMore) return; // Prevent concurrent requests
    setLoading(true);

    
    console.log(`Fetching entries for email: ${email}`); // Debugging: log the email

    try {
      const fetchedEntries = await Promise.all(
        dateList.map((date) => fetchDataByDateAndEmail(date, email))
      );
      console.log(dateList);
      console.log(email);

      console.log("Fetched Entries:", fetchedEntries); // Debugging: log fetched entries

      const validEntries = fetchedEntries.filter(entry => entry.imageUrl); // Filter out empty entries

      if (validEntries.length === 0) {
        setHasMore(false); // No more data to load
        console.log('no entries');
      } else {
        setEntries((prevEntries) => [...prevEntries, ...validEntries]);
      }
    } catch (error) {
      console.error("Error loading entries:", error);
    } finally {
      setLoading(false);
    }
  };

  // Automatically load entries when the component mounts
  useEffect(() => {
    loadEntries();
  }, [dateList]); // Load entries when dateList changes

  return (
    <div style={{ width: "90%", margin: "0 auto", paddingTop: "20px" }}>
      <InfiniteScroll
        pageStart={0}
        loadMore={loadEntries}
        hasMore={hasMore}
        loader={
          <div style={{ textAlign: "center", margin: "20px 0" }} key={0}>
            Loading ...
          </div>
        }
      >
        <div style={{ display: "flex", flexWrap: "wrap", justifyContent: "center" }}>
          {entries.map((entry, i) => (
            <div
              key={entry.date + i}
              style={{
                border: "1px solid #ddd",
                borderRadius: "10px",
                width: "200px",
                margin: "10px",
                textAlign: "center",
                boxShadow: "0 4px 8px rgba(0, 0, 0, 0.1)",
              }}
            >
              {entry.imageUrl ? (
                <img
                  src={entry.imageUrl}
                  alt={`Diary entry for ${entry.date}`}
                  style={{
                    width: "100%",
                    borderTopLeftRadius: "10px",
                    borderTopRightRadius: "10px",
                    padding: "10px",
                  }}
                />
              ) : (
                <div style={{ padding: "20px", color: "#888" }}>No image available</div>
              )}
              <div style={{ padding: "10px" }}>
                <h5 style={{ margin: "10px 0", fontSize: "1.1em" }}>{entry.date}</h5>
              </div>
            </div>
          ))}
        </div>
      </InfiniteScroll>
    </div>
  );
};

export default ViewData;
