import React, { useState } from 'react';

const AddDataPage = ({ selectedDate, handleUpload, setCurrentPage }) => {
    const [text, setText] = useState("");
    const [images, setImages] = useState([]);

    const handleTextChange = (e) => {
        setText(e.target.value);
    };

    const handleImageChange = (e) => {
        const files = Array.from(e.target.files);
        const imageURLs = files.map(file => URL.createObjectURL(file));
        setImages([...images, ...imageURLs]);
    };

    const handleSave = () => {
        if (selectedDate) {
            const newUpload = { text, images };
            handleUpload(selectedDate.toDateString(), newUpload);
        }
    };

    return (
        <div className="add-data-page">
            <h1>Add Data for {selectedDate.toDateString()}</h1>
            <textarea 
                placeholder="Write your diary entry..." 
                value={text} 
                onChange={handleTextChange} 
            />
            <input type="file" multiple onChange={handleImageChange} />
            <div className="image-preview">
                {images.map((image, index) => (
                    <img key={index} src={image} alt="upload" style={{ width: "100px", margin: "5px" }} />
                ))}
            </div>
            <button onClick={handleSave}>Save</button>
            <button onClick={() => setCurrentPage('calendar')}>Back to Calendar</button>
        </div>
    );
};

export default AddDataPage;
