import React, { useState, useEffect } from 'react';
import "./CalendarPage.css";
import Diary from "../assets/opendiary2.png";

const CalendarPage = ({ setCurrentPage, handleUpload }) => {
    const [selectedDate, setSelectedDate] = useState(new Date()); // Initialize with current date
    const [textValue, setTextValue] = useState(`Selected Date: ${new Date().toDateString()}`);
    const [data, setData] = useState(''); // State to hold form input
    const [isAddingData, setIsAddingData] = useState(false); // State to toggle between calendar and add data form

    // Function to handle date change
    const handleDateChange = (event) => {
        const newDate = new Date(event.target.value);
        setSelectedDate(newDate);
        setTextValue(`Selected Date: ${newDate.toDateString()}`);
    };

    // Effect to reset selectedDate if it's null or invalid
    useEffect(() => {
        if (!selectedDate || isNaN(selectedDate.getTime())) {
            setSelectedDate(new Date()); // Set to current date if no valid date is selected
        }
    }, [selectedDate]);

    // Handle input change
    const handleDataChange = (event) => {
        setData(event.target.value);
    };

    // Handle form submission
    const handleSubmit = (event) => {
        event.preventDefault();
        if (selectedDate && data.trim() !== '') {
            handleUpload(selectedDate, data); // Call the handleUpload function passed via props
            setData(''); // Clear the text area after submission
            setTextValue(`Selected Date: ${new Date().toDateString()}`); // Reset text value
            setIsAddingData(false); // Hide the add data form
        } else {
            alert('Please enter data before adding.');
        }
    };

    return (
        <div style={{ padding: '20px', marginTop: '2vh' }}>
            <div
                style={{
                    width: '93%',
                    height: '60px',
                    display: 'flex',
                    marginLeft: '50px',
                    justifyContent: 'center',
                    alignItems: 'center',
                }}
            >
                <label style={{ marginRight: '10px' }}>Select a Date:</label>
                <input
                    type="date"
                    value={selectedDate.toISOString().substr(0, 10)}
                    onChange={handleDateChange}
                    style={{ padding: '5px', borderRadius: '5px', border: '1px solid #ccc' }}
                />
            </div>
            <div
                style={{
                    width: '803px',
                    height: '552.5px',
                    margin: 'auto',
                    padding: '20px',
                    backgroundImage: `url(${Diary})`,
                    backgroundPosition: 'center',
                    backgroundSize: 'cover'
                }}
            >
                {/* Container for Left and Right Divs */}
                <div style={{ display: 'flex', justifyContent: 'space-between' }}>
                    {/* Left Div for Random Images */}
                    <div
                        style={{
                            width: '44%',
                            height: '510px',
                            marginLeft: '50px',
                            backgroundColor: '#f0f0f0',
                            padding: '10px',
                        }}
                    >
                        <h2>Random Images</h2>
                        <img src="https://source.unsplash.com/random/200x200" alt="Random 1" style={{ width: '100%', marginBottom: '10px' }} />
                        <img src="https://source.unsplash.com/random/201x201" alt="Random 2" style={{ width: '100%', marginBottom: '10px' }} />
                        <img src="https://source.unsplash.com/random/202x202" alt="Random 3" style={{ width: '100%', marginBottom: '10px' }} />
                    </div>

                    {/* Right Div for Text Area */}
                    <div
                        style={{
                            width: '47%',
                            height: '500px',
                            marginTop: '5px',
                            marginRight: '5px',
                            backgroundColor: 'transparent',
                            padding: '10px',
                        }}
                    >
                        <textarea
                            style={{
                                height: '88%',
                                width: '100%',
                                padding: '10px',
                                border: 'none',
                                outline: 'none',
                                resize: 'none',
                                boxSizing: 'border-box',
                                backgroundColor: 'transparent',
                            }}
                            value={data}
                            onChange={handleDataChange}
                        />
                        <button
                            onClick={handleSubmit}
                            style={{
                                width: '90%',
                                padding: '10px',
                                borderRadius: '5px',
                                border: 'none',
                                backgroundColor: '#4CAF50',
                                color: '#fff',
                                cursor: 'pointer',
                                marginTop: '10px'
                            }}
                        >
                            Add Data
                        </button>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default CalendarPage;
