import React, { useState, useRef } from 'react';
import axios from 'axios';
import Diary from "../assets/opendiary2.png";

const AddData = ({ setCurrentPage}) => {
    const [selectedDate] = useState(new Date()); // Initialize with current date
    const [textValue] = useState(new Date().toLocaleDateString(undefined, {
        day: '2-digit',
        month: 'short',
        year: 'numeric'
    }));
    const [data, setData] = useState(''); // State to hold form input
    const [image, setImage] = useState([]); // State to hold uploaded images
    const [entry, setEntry] = useState('');
    const imageInput = useRef(null);

    // Handle form submission
    const handleSubmit = async (event) => {
        event.preventDefault();
        const userId = localStorage.getItem('userId'); // Assuming user ID is stored in localStorage
        const entryId = `entry-${Date.now()}`; // Generate unique entry ID
    
        // If image exists, upload it first
        let imageUrl = null;
        if (image) {
          try {
            const formData = new FormData();
            formData.append('image', image);
    
            const uploadResponse = await axios.post('/api/diary/upload-image', formData, {
              headers: { 'Content-Type': 'multipart/form-data' }
            });
            imageUrl = uploadResponse.data.imageUrl; // Get the S3 image URL
          } catch (error) {
            console.error('Error uploading image:', error);
          }
        }
    
        // Now store the diary entry (with image URL if it was uploaded)
        try {
          await axios.post('/api/diary/add-entry', {
            entryId,
            userId,
            entryDate: selectedDate.toISOString(),
            content: entry,
            imageUrl, // Store image URL in DynamoDB
          });
          console.log('Diary entry added successfully');
          setCurrentPage('viewdata'); // Redirect to the view data page
        } catch (error) {
          console.error('Error adding diary entry:', error);
        }
      };

    // Handle image upload
    const handleImageUpload = (event) => {
        const files = Array.from(event.target.files);
        const newImages = files.map(file => URL.createObjectURL(file));
        setImage(prevImages => [...prevImages, ...newImages]);
    };


    const handleAddImage = () => {
        imageInput.current.click();
    }

    // const handleImageUpload = (event) => {
    //     setImage(event.target.files[0]); // Store the image file
    //   };

    return (
        <div style={{ padding: '20px' }}>
            <div
                style={{
                    width: '93%',
                    height: '60px',
                    display: 'flex',
                    marginLeft: '50px',
                    justifyContent: 'center',
                    alignItems: 'center',
                }}
            >
                {/* Display current date only */}
            </div>
            <div
                style={{
                    width: '803px',
                    height: '552.5px',
                    margin: 'auto',
                    padding: '20px',
                    backgroundImage: `url(${Diary})`,
                    backgroundPosition: 'center',
                    backgroundSize: 'cover'
                }}
            >
                {/* Container for Left and Right Divs */}
                <div style={{ display: 'flex', justifyContent: 'space-between' }}>
                    {/* Left Div for Random Images */}
                    <div
                        style={{
                            width: '44%',
                            height: '510px',
                            marginLeft: '50px',
                            backgroundColor: 'transparent',
                            padding: '10px',
                            overflowY: 'auto', // Enable scrolling if content overflows
                        }}
                    >
                        <input
                            type="file"
                            accept="image/*"
                            ref={imageInput}
                            multiple
                            onChange={handleImageUpload}
                            style={{ display: 'none' }}
                        />
                        {image.map((image, index) => (
                            <img key={index} src={image} alt={`Uploaded ${index}`} style={{ width: '100%', marginBottom: '10px' }} />
                        ))}
                    </div>

                    {/* Right Div for Text Area */}
                    <div
                        style={{
                            width: '47%',
                            height: '500px',
                            marginTop: '5px',
                            marginRight: '5px',
                            backgroundColor: 'transparent',
                            padding: '10px',
                        }}
                    >
                        <span style={{ marginRight: '10px',color:'black'}}>{textValue}</span>
                        <textarea
                            style={{
                                height: '100%',
                                width: '100%',
                                padding: '10px',
                                border: 'none',
                                outline: 'none',
                                resize: 'none',
                                boxSizing: 'border-box',
                                backgroundColor: 'transparent',fontFamily: 'Dancing Script, cursive'
                            }}
                            value={data}
                            onChange={(e) => setEntry(e.target.value)}
                        />
                    </div>
                </div>
                <div style={{ display: 'flex', justifyContent: 'center', marginTop: '20px' }}>
                    <button
                        onClick={handleAddImage}
                        style={{
                            width: '40%',
                            padding: '10px',
                            borderRadius: '5px',
                            border: 'none',
                            backgroundColor: '#4CAF50',
                            color: '#fff',
                            cursor: 'pointer',
                            marginRight: '10px', // Add margin between buttons
                        }}
                    >
                        Add Images
                    </button>
                    <button
                        onClick={handleSubmit}
                        style={{
                            width: '40%',
                            padding: '10px',
                            borderRadius: '5px',
                            border: 'none',
                            backgroundColor: '#4CAF50',
                            color: '#fff',
                            cursor: 'pointer',
                        }}
                    >
                        Submit Data
                    </button>
                </div>
            </div>
        </div>
    );
};

export default AddData;
