import React, { useState, useEffect } from 'react';

export default function SignUp() {
  const [mode, setMode] = useState('light');
  const [showCustomTheme, setShowCustomTheme] = useState(true);
  const [emailError, setEmailError] = useState(false);
  const [emailErrorMessage, setEmailErrorMessage] = useState('');
  const [passwordError, setPasswordError] = useState(false);
  const [passwordErrorMessage, setPasswordErrorMessage] = useState('');
  const [nameError, setNameError] = useState(false);
  const [nameErrorMessage, setNameErrorMessage] = useState('');

  // This code only runs on the client side, to determine the system color preference
  useEffect(() => {
    // Check if there is a preferred mode in localStorage
    const savedMode = localStorage.getItem('themeMode');
    if (savedMode) {
      setMode(savedMode);
    } else {
      // If no preference is found, it uses system preference
      const systemPrefersDark = window.matchMedia(
        '(prefers-color-scheme: dark)'
      ).matches;
      setMode(systemPrefersDark ? 'dark' : 'light');
    }
  }, []);

  const toggleColorMode = () => {
    const newMode = mode === 'dark' ? 'light' : 'dark';
    setMode(newMode);
    localStorage.setItem('themeMode', newMode); // Save the selected mode to localStorage
  };

  const toggleCustomTheme = () => {
    setShowCustomTheme((prev) => !prev);
  };

  const validateInputs = () => {
    const email = document.getElementById('email');
    const password = document.getElementById('password');
    const name = document.getElementById('name');

    let isValid = true;

    if (!email.value || !/\S+@\S+\.\S+/.test(email.value)) {
      setEmailError(true);
      setEmailErrorMessage('Please enter a valid email address.');
      isValid = false;
    } else {
      setEmailError(false);
      setEmailErrorMessage('');
    }

    if (!password.value || password.value.length < 6) {
      setPasswordError(true);
      setPasswordErrorMessage('Password must be at least 6 characters long.');
      isValid = false;
    } else {
      setPasswordError(false);
      setPasswordErrorMessage('');
    }

    if (!name.value || name.value.length < 1) {
      setNameError(true);
      setNameErrorMessage('Name is required.');
      isValid = false;
    } else {
      setNameError(false);
      setNameErrorMessage('');
    }

    return isValid;
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    const data = new FormData(event.currentTarget);
    console.log({
      name: data.get('name'),
      lastName: data.get('lastName'),
      email: data.get('email'),
      password: data.get('password'),
    });
  };

  return (
    <div className={`sign-up-container ${mode}`}>
      <div className="card">
        <div className="sitemark-icon"> {/* Replace with your own icon or image */} </div>
        <h1 className="title">Sign up</h1>
        <form onSubmit={handleSubmit} className="form">
          <div className="form-control">
            <label htmlFor="name">Full name</label>
            <input
              type="text"
              id="name"
              name="name"
              placeholder="Jon Snow"
              required
              className={nameError ? 'error' : ''}
            />
            {nameError && <span className="error-message">{nameErrorMessage}</span>}
          </div>
          <div className="form-control">
            <label htmlFor="email">Email</label>
            <input
              type="email"
              id="email"
              name="email"
              placeholder="your@email.com"
              required
              className={emailError ? 'error' : ''}
            />
            {emailError && <span className="error-message">{emailErrorMessage}</span>}
          </div>
          <div className="form-control">
            <label htmlFor="password">Password</label>
            <input
              type="password"
              id="password"
              name="password"
              placeholder="••••••"
              required
              className={passwordError ? 'error' : ''}
            />
            {passwordError && <span className="error-message">{passwordErrorMessage}</span>}
          </div>
          <div className="form-control">
            <label>
              <input type="checkbox" value="allowExtraEmails" />
              I want to receive updates via email.
            </label>
          </div>
          <button
            type="submit"
            className="submit-button"
            onClick={validateInputs}
          >
            Sign up
          </button>
          <p className="login-link">
            Already have an account?{' '}
            <a href="/material-ui/getting-started/templates/sign-in/">Sign in</a>
          </p>
        </form>
        <div className="divider">
          <span>or</span>
        </div>
        <div className="social-buttons">
          <button
            className="social-button google"
            onClick={() => alert('Sign up with Google')}
          >
            Sign up with Google
          </button>
          <button
            className="social-button facebook"
            onClick={() => alert('Sign up with Facebook')}
          >
            Sign up with Facebook
          </button>
        </div>
      </div>
    </div>
  );
}
