import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

export default function AuthPage() {
  const [isLogin, setIsLogin] = useState(true);
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [name, setName] = useState('');
  const [signup, setSignup] = useState('')
  const [signin, setSignin] = useState('')
  const navigate = useNavigate();

  const handleSubmit = async (event) => {
    event.preventDefault();
    const url = isLogin ? 'http://localhost:5000/api/auth/sign-in' : 'http://localhost:5000/api/auth/sign-up';

    try {
      const payload = isLogin ? { email, password, signup } : { email, password, name, signin };
      const response = await axios.post(url, payload);
      if (isLogin) {
        const token = response.data.token;
        console.log('Sign-in success, token:', token);
        localStorage.setItem('email', email);
        localStorage.setItem('token', token);
        navigate('/diary');
      } else {
        console.log('Sign-up success:', response.data.message);
        setIsLogin(true);
      }
    } catch (error) {
      console.error('Error during auth:', error.response?.data?.error || error.message);
    }
  };

  return (
    <div style={{ '--input-focus': '#2d8cf0', '--font-color': '#323232', '--font-color-sub': '#666', '--bg-color': '#fff', '--bg-color-alt': '#666', '--main-color': '#323232', position: 'relative', display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
      <div style={{ transform: 'translateY(200px)', position: 'relative', display: 'flex', flexDirection: 'column', justifyContent: 'center', alignItems: 'center', gap: '30px', width: '50px', height: '20px' }}>
        <label style={{ display: 'flex', flexDirection: 'column', alignItems: 'center', position: 'relative', width: '100%', height: '100%' }}>
          <input
            type="checkbox"
            checked={!isLogin}
            onChange={() => setIsLogin(!isLogin)}
            style={{ opacity: 0, width: 0, height: 0 }}
          />

          <div style={{ width: '300px', height: '350px', position: 'relative', backgroundColor: 'transparent', perspective: '1000px', textAlign: 'center', transition: 'transform 0.8s', transformStyle: 'preserve-3d', transform: isLogin ? 'none' : 'rotateY(180deg)' }}>
            <div style={{ padding: '20px', position: 'absolute', display: 'flex', flexDirection: 'column', justifyContent: 'center', backfaceVisibility: 'hidden', backgroundColor: 'lightgrey', gap: '20px', borderRadius: '5px', border: '2px solid var(--main-color)', boxShadow: '4px 4px var(--main-color)', transform: isLogin ? 'none' : 'rotateY(180deg)' }}>
              <div style={{ margin: '20px 0', fontSize: '25px', fontWeight: 900, textAlign: 'center', color: 'var(--main-color)' }}>{isLogin ? 'Log in' : 'Sign up'}</div>
              <form onSubmit={handleSubmit} style={{ display: 'flex', flexDirection: 'column', alignItems: 'center', gap: '20px' }}>
                {!isLogin && (
                  <input
                    type="text"
                    placeholder="Name"
                    value={name}
                    onChange={(e) => setName(e.target.value)}
                    style={{ width: '250px', height: '40px', borderRadius: '5px', border: '2px solid var(--main-color)', backgroundColor: 'var(--bg-color)', boxShadow: '4px 4px var(--main-color)', fontSize: '15px', fontWeight: 600, color: 'var(--font-color)', padding: '5px 10px', outline: 'none' }}
                  />
                )}
                <input
                  type="email"
                  placeholder="Email"
                  value={email}
                  onChange={(e) => setEmail(e.target.value)}
                  style={{ width: '250px', height: '40px', borderRadius: '5px', border: '2px solid var(--main-color)', backgroundColor: 'var(--bg-color)', boxShadow: '4px 4px var(--main-color)', fontSize: '15px', fontWeight: 600, color: 'var(--font-color)', padding: '5px 10px', outline: 'none' }}
                  required
                />
                <input
                  type="password"
                  placeholder="Password"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  style={{ width: '250px', height: '40px', borderRadius: '5px', border: '2px solid var(--main-color)', backgroundColor: 'var(--bg-color)', boxShadow: '4px 4px var(--main-color)', fontSize: '15px', fontWeight: 600, color: 'var(--font-color)', padding: '5px 10px', outline: 'none' }}
                  required
                />
                {!isLogin && (
                <p type="signup">sign up</p>
                )}
                <button onClick={handleSubmit} style={{ margin: '20px 0', width: '120px', height: '40px', borderRadius: '5px', border: '2px solid var(--main-color)', backgroundColor: 'var(--bg-color)', boxShadow: '4px 4px var(--main-color)', fontSize: '17px', fontWeight: 600, color: 'var(--font-color)', cursor: 'pointer' }}>
                  {isLogin ? "Let's go!" : 'Confirm!'}
                </button>
              </form>
            </div>
          </div>
        </label>
      </div>
    </div>
  );
}
