import React, { useState } from 'react';
import Calendar from "react-calendar";
import "react-calendar/dist/Calendar.css";
import "./CalendarPage.css";
import Diary from "./SignIn/Diary.png";

const CalendarPage = ({ setCurrentPage }) => {
    const [selectedDate, setSelectedDate] = useState(); // State to hold selected date

    // Function to handle date click
    const Date_Click_Fun = (date) => {
        setSelectedDate(date);
    };

    return (
        <div style={{ padding: '20px', marginTop: '10vh' }}>
            <div
                style={{
                    width: '464px',
                    height: '617px',
                    margin: 'auto',
                    padding: '20px',
                    boxShadow: '0 5px 15px rgba(0, 0, 0, 0.1)',
                    backgroundImage: `url(${Diary})`,
                    backgroundPosition: 'center',
                    backgroundSize: 'cover'
                }}
            >
                {/* Top Div for Date Selection */}
                <div style={{ textAlign: 'center', marginBottom: '20px' }}>
                    <h1>Select a Date</h1>
                    <Calendar
                        onClickDay={Date_Click_Fun}
                        value={selectedDate} // Show selected date
                        className="black"
                    />
                </div>

                {/* Left Div for Random Images */}
                <div
                    style={{
                        float: 'left',
                        width: '30%',
                        height: '400px',
                        backgroundColor: '#f0f0f0',
                        padding: '10px',
                        overflowY: 'scroll',
                    }}
                >
                    <h2>Random Images</h2>
                    <img src="https://source.unsplash.com/random/200x200" alt="Random 1" style={{ width: '100%', marginBottom: '10px' }} />
                    <img src="https://source.unsplash.com/random/201x201" alt="Random 2" style={{ width: '100%', marginBottom: '10px' }} />
                    <img src="https://source.unsplash.com/random/202x202" alt="Random 3" style={{ width: '100%', marginBottom: '10px' }} />
                </div>

                {/* Right Div for Text */}
                <div
                    style={{
                        float: 'right',
                        width: '30%',
                        height: '400px',
                        backgroundColor: '#f0f0f0',
                        padding: '10px',
                        overflowY: 'scroll',
                    }}
                >
                    <h2>Selected Date Info</h2>
                    {selectedDate ? (
                        <p>
                            You have selected: <strong>{selectedDate.toDateString()}</strong>
                        </p>
                    ) : (
                        <p>Please select a date from the calendar above.</p>
                    )}
                </div>

                {/* Clearfix for Floats */}
                <div style={{ clear: 'both' }}></div>

                {/* Show buttons only if a date is selected */}
                {selectedDate && (
                    <div className="button-group" style={{ textAlign: 'center', marginTop: '20px' }}>
                        <button onClick={() => setCurrentPage('addData')}>Add Data</button>
                        <button onClick={() => setCurrentPage('viewData')}>View Data</button>
                    </div>
                )}
            </div>
        </div>
    );
};

export default CalendarPage;
