import React, { useState } from 'react';
import Navbar from './components/Navbar';  // Import Navbar
import CalendarPage from './components/CalendarPage';
import AddDataPage from './components/AddDataPage';
import ViewDataPage from './components/ViewDataPage';
import './App.css';

const App = () => {
    const [currentPage, setCurrentPage] = useState('calendar');
    const [selectedDate, setSelectedDate] = useState(null);
    const [uploads, setUploads] = useState({});

    const handleAddData = () => {
        if (selectedDate) {
            setCurrentPage('addData');
        }
    };

    const handleViewData = () => {
        setCurrentPage('viewData');
    };

    const handleUpload = (date, newUpload) => {
        setUploads(prevUploads => ({
            ...prevUploads,
            [date]: newUpload
        }));
        setCurrentPage('calendar');
    };

    return (
        <div className="app">
            <Navbar setCurrentPage={setCurrentPage} />  {/* Add Navbar */}

            <div className="content">
                {currentPage === 'calendar' && (
                    <CalendarPage 
                        setCurrentPage={setCurrentPage}
                        setSelectedDate={setSelectedDate}
                        selectedDate={selectedDate}
                        handleAddData={handleAddData}
                        handleViewData={handleViewData}
                    />
                )}
                {currentPage === 'addData' && (
                    <AddDataPage 
                        selectedDate={selectedDate} 
                        handleUpload={handleUpload} 
                        setCurrentPage={setCurrentPage} 
                    />
                )}
                {currentPage === 'viewData' && (
                    <ViewDataPage 
                        uploads={uploads} 
                        setCurrentPage={setCurrentPage} 
                    />
                )}
            </div>
        </div>
    );
};

export default App;
