import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const Profile = () => {
    const [userProfile, setUserProfile] = useState({
        name: "John Doe",
        email: "john.doe@example.com",
        password: "********",
        profilePicture: "https://via.placeholder.com/150",  // Placeholder image
    });

    const [isEditing, setIsEditing] = useState(false);
    const [newProfilePicture, setNewProfilePicture] = useState(null);
    const navigate = useNavigate();  // Initialize useNavigate

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setUserProfile({
            ...userProfile,
            [name]: value,
        });
    };

    const handleImageUpload = (e) => {
        const file = e.target.files[0];
        if (file) {
            setNewProfilePicture(URL.createObjectURL(file));
        }
    };

    const handleSave = () => {
        // Here, add API call to update the profile on the server.
        alert("Profile updated successfully!");
        setIsEditing(false);
        if (newProfilePicture) {
            setUserProfile({
                ...userProfile,
                profilePicture: newProfilePicture,
            });
        }
    };

    const handleBack = () => {
        navigate(-1);  // Navigate to the previous page (-1 goes back in history)
    };

    return (
        <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center', marginTop: '50px' }}>
            {/* Profile Picture */}
            <div style={{ marginBottom: '20px', position: 'relative' }}>
                <img
                    src={newProfilePicture || userProfile.profilePicture}
                    alt="Profile"
                    style={{ borderRadius: '50%', width: '150px', height: '150px' }}
                />
                {isEditing && (
                    <input
                        type="file"
                        accept="image/*"
                        onChange={handleImageUpload}
                        style={{
                            position: 'absolute',
                            bottom: '-10px',
                            right: '-10px',
                            cursor: 'pointer',
                            opacity: 0,
                            width: '100%',
                            height: '100%',
                        }}
                    />
                )}
            </div>

            {/* User Name */}
            {isEditing ? (
                <input
                    type="text"
                    name="name"
                    value={userProfile.name}
                    onChange={handleInputChange}
                    style={{ fontSize: '24px', padding: '5px', marginBottom: '10px' }}
                />
            ) : (
                <h1 style={{ fontSize: '36px', color: '#4CAF50' }}>{userProfile.name}</h1>
            )}

            {/* Email */}
            {isEditing ? (
                <input
                    type="email"
                    name="email"
                    value={userProfile.email}
                    onChange={handleInputChange}
                    style={{ fontSize: '18px', padding: '5px', marginBottom: '10px' }}
                />
            ) : (
                <p style={{ fontSize: '24px', color: '#555' }}>{userProfile.email}</p>
            )}

            {/* Password */}
            {isEditing && (
                <input
                    type="password"
                    name="password"
                    value={userProfile.password}
                    onChange={handleInputChange}
                    style={{ fontSize: '18px', padding: '5px', marginBottom: '10px' }}
                />
            )}

            {/* Edit or Save Button */}
            {isEditing ? (
                <button
                    style={{
                        padding: '10px 20px',
                        borderRadius: '5px',
                        border: 'none',
                        backgroundColor: '#4CAF50',
                        color: '#fff',
                        cursor: 'pointer',
                        marginTop: '20px',
                        fontSize: '18px',
                    }}
                    onClick={handleSave}
                >
                    Save Changes
                </button>
            ) : (
                <button
                    style={{
                        padding: '10px 20px',
                        borderRadius: '5px',
                        border: 'none',
                        backgroundColor: '#4CAF50',
                        color: '#fff',
                        cursor: 'pointer',
                        marginTop: '20px',
                        fontSize: '18px',
                    }}
                    onClick={() => setIsEditing(true)}
                >
                    Edit Profile
                </button>
            )}

            {/* Back Button */}
            <button
                style={{
                    padding: '10px 20px',
                    borderRadius: '5px',
                    border: 'none',
                    backgroundColor: '#f44336',
                    color: '#fff',
                    cursor: 'pointer',
                    marginTop: '20px',
                    fontSize: '18px',
                }}
                onClick={handleBack}  // Call handleBack onClick
            >
                Back
            </button>
        </div>
    );
};

export default Profile;
