import React, { useState, useEffect } from "react";
import './CalendarPage.css'; // Assuming you have external CSS for styling

const CalendarPage = () => {
  const [currentMonth, setCurrentMonth] = useState(new Date().getMonth());
  const [currentYear, setCurrentYear] = useState(new Date().getFullYear());
  const [activeMonth, setActiveMonth] = useState(true);

  const daysOfWeek = ["Sun", "Mon", "Tue", "Wed", "Thu", "Fri", "Sat"];
  const months = [
    "Jan", "Feb", "Mar", "Apr", "May", "Jun", "Jul", "Aug", "Sep", "Oct", "Nov", "Dec"
  ];

  const showCurrentMonth = (year, month) => {
    setCurrentYear(year);
    setCurrentMonth(month);
  };

  useEffect(() => {
    showCurrentMonth(currentYear, currentMonth);
  }, [currentYear, currentMonth]); // Add missing dependencies

  const nextMonth = () => {
    setActiveMonth(!activeMonth);
    if (currentMonth === 11) {
      setCurrentMonth(0);
      setCurrentYear(currentYear + 1);
    } else {
      setCurrentMonth(currentMonth + 1);
    }
  };

  const prevMonth = () => {
    setActiveMonth(!activeMonth);
    if (currentMonth === 0) {
      setCurrentMonth(11);
      setCurrentYear(currentYear - 1);
    } else {
      setCurrentMonth(currentMonth - 1);
    }
  };

  return (
    <div className="calendar" data-theme={months[currentMonth].toLowerCase()}>
      <div className="header active">
        <button
          className="prev-month"
          title="Previous Month"
          onClick={prevMonth}
        >
          <i className="arrow left"></i>
        </button>
        <div className="text" data-render="month-year">
          <span>{months[currentMonth]} {currentYear}</span>
        </div>
        <button
          className="next-month"
          title="Next Month"
          onClick={nextMonth}
        >
          <i className="arrow right"></i>
        </button>
      </div>
      <div className="months" data-flow={activeMonth ? "left" : "right"}>
        <div className="month month-a" data-active={activeMonth}>
          <CalendarTable
            year={currentYear}
            month={currentMonth}
            daysOfWeek={daysOfWeek}
          />
        </div>
        <div className="month month-b" data-active={!activeMonth}>
          {/* For future dynamic rendering */}
        </div>
      </div>
    </div>
  );
};

const CalendarTable = ({ year, month, daysOfWeek }) => {
  const daysInMonth = new Date(year, month + 1, 0).getDate();
  const firstDayOfMonth = new Date(year, month, 1).getDay();

  const prevMonthDays = month === 0
    ? new Date(year - 1, 12, 0).getDate()
    : new Date(year, month, 0).getDate();

  const currentDate = new Date();
  const today = currentDate.getDate();
  const isCurrentMonth = currentDate.getMonth() === month && currentDate.getFullYear() === year;

  let daysArray = [];
  let dayCount = 1;

  for (let row = 0; row < 6; row++) {
    let rowArray = [];
    for (let col = 0; col < 7; col++) {
      let dayCell;

      if (row === 0 && col < firstDayOfMonth) {
        dayCell = <div className="cell disable">{prevMonthDays - firstDayOfMonth + col + 1}</div>;
      } else if (dayCount > daysInMonth) {
        dayCell = <div className="cell disable">{dayCount++ - daysInMonth}</div>;
      } else {
        dayCell = (
          <div className={`cell ${isCurrentMonth && dayCount === today ? 'active' : ''}`}>
            <span>{dayCount++}</span>
          </div>
        );
      }

      rowArray.push(dayCell);
    }
    daysArray.push(<div className="row">{rowArray}</div>);
  }

  return (
    <div className="table">
      <div className="row head">
        {daysOfWeek.map((day, index) => (
          <div className="cell" key={index}>
            {day}
          </div>
        ))}
      </div>
      {daysArray}
    </div>
  );
};

export default CalendarPage;
