import React, { useState } from 'react';
import Calendar from "react-calendar";
import 'react-calendar/dist/Calendar.css';
import './App.css';

const App = () => {
    const [currentPage, setCurrentPage] = useState('calendar'); // To switch between pages
    const [selectedDate, setSelectedDate] = useState(null);
    const [uploads, setUploads] = useState({});

    // Function to switch to the "add data" page
    const handleAddData = () => {
        if (selectedDate) {
            setCurrentPage('addData');
        }
    };

    // Function to switch to the "view data" page
    const handleViewData = () => {
        setCurrentPage('viewData');
    };

    // To store data uploads
    const handleUpload = (date, newUpload) => {
        setUploads(prevUploads => ({
            ...prevUploads,
            [date]: newUpload
        }));
        setCurrentPage('calendar'); // Return to calendar after upload
    };

    // Function to switch pages
    const changePage = (page) => {
        setCurrentPage(page);
    };

    return (
        <div className="app">
            {currentPage === 'calendar' && (
                <CalendarPage 
                    setCurrentPage={setCurrentPage} 
                    setSelectedDate={setSelectedDate} 
                    selectedDate={selectedDate} 
                    handleAddData={handleAddData} 
                    handleViewData={handleViewData} 
                />
            )}
            {currentPage === 'addData' && (
                <AddDataPage 
                    selectedDate={selectedDate} 
                    handleUpload={handleUpload} 
                    setCurrentPage={setCurrentPage} 
                />
            )}
            {currentPage === 'viewData' && (
                <ViewDataPage 
                    uploads={uploads} 
                    setCurrentPage={setCurrentPage} 
                />
            )}
        </div>
    );
};
