import React, { useState } from 'react';
import Calendar from "react-calendar";
import "react-calendar/dist/Calendar.css";
import "./CalendarPage.css";
import Diary from "./SignIn/Diary.png"

const CalendarPage = (setCurrentPage) => {
    const [selectedDate, setSelectedDate] = useState();  // State to hold selected date

    // Function to handle date click
    const Date_Click_Fun = (date) => {
        setSelectedDate(date);
    };


    return (
        <div style={{ padding: '20px', marginTop: '10vh'}}>
      <div style={{ width: '464px',height:'617px', margin: 'auto', padding: '20px', boxShadow: '0 5px 15px rgba(0, 0, 0, 0.1)', backgroundImage: `url(${Diary})`,backgroundPosition: 'center', backgroundSize: 'cover'}}>
        <div className="calendar-page">
            <h1>Select a Date from the Calendar</h1>
            <div className="calendar-container">
                <Calendar className="black"
                    onClickDay={Date_Click_Fun}
                    value={selectedDate}  // Show selected date on the calendar
                />
            </div>
            {/* Show buttons only if a date is selected */}
            {selectedDate && (
                <div className="button-group">
                    <button onClick={setCurrentPage='addData'}>Add Data</button>
                    <button onClick={setCurrentPage='viewData'}>View Data</button>
                </div>
            )}
        </div>
        </div>
        </div>
    );
};

export default CalendarPage;
