import React, { useState } from "react";

const CalendarPage = () => {
  const [isFlipped, setIsFlipped] = useState(false);
  const [selectedDate, setSelectedDate] = useState("Jan 15th, 2016");
  const [selectedTime, setSelectedTime] = useState("6:35 PM");
  const [eventDescription, setEventDescription] = useState("");

  const flipCalendar = () => {
    setIsFlipped(!isFlipped);
  };

  const handleDateClick = (date) => {
    setSelectedDate(date);
    flipCalendar();
  };

  const handleInputChange = (e) => {
    setEventDescription(e.target.value);
  };

  const handleSave = () => {
    alert("Event Saved: " + eventDescription);
    flipCalendar();
  };

  const handleDismiss = () => {
    setEventDescription("");
    flipCalendar();
  };

  const calendarStyles = {
    container: {
      display: "flex",
      justifyContent: "center",
      alignItems: "center",
      height: "100vh",
      fontFamily: "'Roboto', sans-serif",
      background: "#dfebed",
    },
    calendar: {
      width: "400px",
      height: "501px",
      backgroundColor: "#2b4450",
      borderRadius: "4px",
      perspective: "1000px",
      transformStyle: "preserve-3d",
      transform: isFlipped ? "rotateY(180deg)" : "rotateY(0deg)",
      transition: "transform 0.9s",
      boxShadow: "0 5px 20px rgba(0, 0, 0, 0.3)",
      position: "relative",
    },
    front: {
      position: "absolute",
      width: "100%",
      height: "100%",
      backfaceVisibility: "hidden",
    },
    back: {
      position: "absolute",
      width: "100%",
      height: "100%",
      transform: "rotateY(180deg)",
      backfaceVisibility: "hidden",
    },
    currentDate: {
      display: "flex",
      justifyContent: "space-between",
      padding: "30px 40px",
      borderBottom: "1px solid rgba(73, 114, 133, 0.6)",
      color: "#dfebed",
    },
    h1: {
      fontSize: "1.4em",
      fontWeight: "300",
    },
    weekDays: {
      display: "flex",
      justifyContent: "space-between",
      padding: "30px 40px",
      color: "#dfebed",
      fontWeight: "600",
    },
    weeks: {
      padding: "0 40px",
      color: "#fff",
    },
    weekRow: {
      display: "flex",
      justifyContent: "space-between",
      marginBottom: "20px",
    },
    day: {
      padding: "10px",
      cursor: "pointer",
    },
    activeDay: {
      backgroundColor: "#f78536",
      borderRadius: "50%",
    },
    lastMonth: {
      opacity: 0.3,
    },
    input: {
      width: "100%",
      padding: "30px 40px",
      borderBottom: "1px solid rgba(73, 114, 133, 0.6)",
      background: "none",
      color: "#dfebed",
      fontSize: "1.4em",
      fontWeight: "300",
    },
    info: {
      padding: "30px 40px",
      color: "#dfebed",
      fontWeight: "600",
    },
    date: {
      display: "flex",
      justifyContent: "space-between",
      marginBottom: "40px",
    },
    address: {
      marginBottom: "40px",
    },
    actions: {
      display: "flex",
      borderTop: "1px solid rgba(73, 114, 133, 0.6)",
      position: "absolute",
      width: "100%",
      bottom: "0",
    },
    button: {
      width: "50%",
      padding: "30px 0",
      textTransform: "uppercase",
      fontWeight: "600",
      color: "#fff",
      background: "none",
      cursor: "pointer",
    },
    buttonHover: {
      backgroundColor: "#497285",
    },
    buttonActive: {
      backgroundColor: "#5889a0",
    },
  };

  return (
    <div style={calendarStyles.container}>
      <div style={calendarStyles.calendar}>
        <div style={calendarStyles.front}>
          <div style={calendarStyles.currentDate}>
            <h1 style={calendarStyles.h1}>Friday 15th</h1>
            <h1 style={calendarStyles.h1}>January 2016</h1>
          </div>
          <div style={calendarStyles.weekDays}>
            <li>MON</li>
            <li>TUE</li>
            <li>WED</li>
            <li>THU</li>
            <li>FRI</li>
            <li>SAT</li>
            <li>SUN</li>
          </div>
          <div style={calendarStyles.weeks}>
            <div style={calendarStyles.weekRow}>
              <span style={calendarStyles.lastMonth}>28</span>
              <span style={calendarStyles.lastMonth}>29</span>
              <span style={calendarStyles.lastMonth}>30</span>
              <span style={calendarStyles.lastMonth}>31</span>
              <span style={calendarStyles.day} onClick={() => handleDateClick("Jan 1st, 2016")}>01</span>
              <span style={calendarStyles.day}>02</span>
              <span style={calendarStyles.day}>03</span>
            </div>
            <div style={calendarStyles.weekRow}>
              <span style={calendarStyles.day}>04</span>
              <span style={calendarStyles.day}>05</span>
              <span style={calendarStyles.day} onClick={() => handleDateClick("Jan 6th, 2016")}>06</span>
              <span style={calendarStyles.day}>07</span>
              <span style={calendarStyles.day}>08</span>
              <span style={calendarStyles.day}>09</span>
              <span style={calendarStyles.day}>10</span>
            </div>
            <div style={calendarStyles.weekRow}>
              <span style={calendarStyles.day}>11</span>
              <span style={calendarStyles.day}>12</span>
              <span style={calendarStyles.day}>13</span>
              <span style={calendarStyles.day}>14</span>
              <span style={{ ...calendarStyles.day, ...calendarStyles.activeDay }}>15</span>
              <span style={calendarStyles.day}>16</span>
              <span style={calendarStyles.day}>17</span>
            </div>
          </div>
        </div>
        <div style={calendarStyles.back}>
          <input
            style={calendarStyles.input}
            placeholder="What's the event?"
            value={eventDescription}
            onChange={handleInputChange}
          />
          <div style={calendarStyles.info}>
            <div style={calendarStyles.date}>
              <p>Date: <span>{selectedDate}</span></p>
              <p>Time: <span>{selectedTime}</span></p>
            </div>
            <div style={calendarStyles.address}>
              <p>Address: <span>129 W 81st St, New York, NY</span></p>
            </div>
            <div>
              <p>Observations: <span>Be there 15 minutes earlier</span></p>
            </div>
          </div>
          <div style={calendarStyles.actions}>
            <button style={calendarStyles.button} onClick={handleSave}>
              Save
            </button>
            <button style={calendarStyles.button} onClick={handleDismiss}>
              Dismiss
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default CalendarPage;
