import React, {useState} from 'react';

const Navbar = ({ setCurrentPage }) => {
    const handleNavigation = (page) => {
        setCurrentPage(page);
    };

    return (
        <nav style={{
            position: 'fixed', 
            top: 0,
            left: 0,
            width: '100%',
            height: '80px', // Set a fixed height
            display: 'flex',
            justifyContent: 'flex-end', // Align items to the right
            alignItems: 'center',
            backgroundColor: 'black', 
            boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)', // Optional shadow for visibility
            zIndex: 1000
        }}>
            <div onClick={() => handleNavigation('calendar')} style={isHovered ? { ...buttonStyle, ...buttonHoverStyle } : buttonStyle} 
        onMouseEnter={handleMouseEnter} 
        onMouseLeave={handleMouseLeave}>WRITE</div>
            <div onClick={() => handleNavigation('viewdata')} style={buttonStyle}>ENTRIES</div>
            <div onClick={() => handleNavigation('cards')} style={buttonStyle}>TIMELINE</div>
            <div onClick={() => handleNavigation('profile')} style={buttonStyle}>PROFILE</div>
        </nav>
    );
};

const buttonStyle = {
    cursor: 'pointer',
    width: '184px',
    height: '45px',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    gap: '1rem',
    fontSize: '1.125em',
    fontWeight: 800,
    letterSpacing: '2px',
    color: '#fff',
    backgroundColor: '#644dff',
    border: '2px solid #4836bb', 
    borderRadius: '0.75rem',
    boxShadow: '0 8px 0 #4836bb',
    transform: 'skew(-10deg)',
    transition: 'all 0.1s ease',
    filter: 'drop-shadow(0 15px 20px rgba(101, 77, 255, 0.39))',
    marginTop: '-10px', // Adjust to move up
    marginRight: '10px'
};

const buttonHoverStyle = {
    letterSpacing: '0px',
    transform: 'skew(-10deg) translateY(8px)',
    boxShadow: '0 0 0 rgba(101, 77, 255, 0.39)', // Update shadow color
};

const [isHovered, setIsHovered] = useState(false);
const handleMouseEnter = () => {
    setIsHovered(true);
};

const handleMouseLeave = () => {
    setIsHovered(false);
};
export default Navbar;
