import React, { useState } from "react";
import InfiniteScroll from "react-infinite-scroller";

const ViewData = () => {
  const [dates, setDates] = useState([]);
  const [nextDate, setNextDate] = useState(new Date());

  const loadDates = () => {
    const newDates = [];
    for (let i = 0; i < 20; i++) { // Generate 20 new dates at a time
      newDates.push(new Date(nextDate));
      setNextDate((prevDate) => new Date(prevDate.setDate(prevDate.getDate() + 1)));
    }
    setDates((prevDates) => [...prevDates, ...newDates]);
  };

  return (
    <div style={{ width: "90%", margin: "0 auto", paddingTop: "20px" }}>
      <InfiniteScroll
        pageStart={0}
        loadMore={loadDates}
        hasMore={true}
        loader={
          <div style={{ textAlign: "center", margin: "20px 0" }} key={0}>
            Loading ...
          </div>
        }
      >
        <div style={{ display: "flex", flexWrap: "wrap", justifyContent: "center" }}>
          {dates.map((date, i) => (
            <div
              key={i}
              style={{
                border: "1px solid #ddd",
                borderRadius: "10px",
                width: "200px",
                margin: "10px",
                textAlign: "center",
                boxShadow: "0 4px 8px rgba(0, 0, 0, 0.1)"
              }}
            >
              <div style={{ padding: "10px" }}>
                <h5 style={{ margin: "10px 0", fontSize: "1.1em" }}>
                  {date.toDateString()}
                </h5>
              </div>
            </div>
          ))}
        </div>
      </InfiniteScroll>
    </div>
  );
};

export default ViewData;
