import React, { useState } from 'react';
import Navbar from './components/Navbar';  // Import Navbar
import CalendarPage from './components/CalendarPage';
import AddDataPage from './components/AddDataPage';
import ViewDataPage from './components/ViewDataPage';
import { Routes, Route } from 'react-router-dom';
import SignIn from './components/SignIn/SignIn';
import SignUp from './components/SignIn/SignUp';
import './App.css';

const App = () => {
    const [selectedDate, setSelectedDate] = useState(null);
    const [uploads, setUploads] = useState({});

    const handleUpload = (date, newUpload) => {
        setUploads(prevUploads => ({
            ...prevUploads,
            [date]: newUpload
        }));
    };

    return (
        <div className="app">
            <Routes>
                {/* Authentication routes */}
                <Route path="/sign-in" element={<SignIn />} />
                <Route path="/sign-up" element={<SignUp />} />

                {/* Diary-related pages */}
                <Route
                    path="/diary"
                    element={
                        <>
                            {/* Navbar is always displayed on the diary pages */}
                            <Navbar />

                            <div className="content">
                                {/* Nested routing to handle different diary pages */}
                                <Routes>
                                    <Route
                                        path="calendar"
                                        element={
                                            <CalendarPage
                                                setSelectedDate={setSelectedDate}
                                                selectedDate={selectedDate}
                                            />
                                        }
                                    />
                                    <Route
                                        path="addData"
                                        element={
                                            <AddDataPage
                                                selectedDate={selectedDate}
                                                handleUpload={handleUpload}
                                            />
                                        }
                                    />
                                    <Route
                                        path="viewData"
                                        element={
                                            <ViewDataPage
                                                uploads={uploads}
                                            />
                                        }
                                    />
                                </Routes>
                            </div>
                        </>
                    }
                />
            </Routes>
        </div>
    );
};

export default App;
