import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import Diary from './Diary.png';

export default function SignUp() {
  const [emailError, setEmailError] = useState(false);
  const [emailErrorMessage, setEmailErrorMessage] = useState('');
  const [passwordError, setPasswordError] = useState(false);
  const [passwordErrorMessage, setPasswordErrorMessage] = useState('');
  const [nameError, setNameError] = useState(false);
  const [nameErrorMessage, setNameErrorMessage] = useState('');
  const navigate = useNavigate();

  const validateInputs = () => {
    const email = document.getElementById('email');
    const password = document.getElementById('password');
    const name = document.getElementById('name');

    let isValid = true;

    if (!email.value || !/\S+@\S+\.\S+/.test(email.value)) {
      setEmailError(true);
      setEmailErrorMessage('Please enter a valid email address.');
      isValid = false;
    } else {
      setEmailError(false);
      setEmailErrorMessage('');
    }

    if (!password.value || password.value.length < 6) {
      setPasswordError(true);
      setPasswordErrorMessage('Password must be at least 6 characters long.');
      isValid = false;
    } else {
      setPasswordError(false);
      setPasswordErrorMessage('');
    }

    if (!name.value || name.value.length < 1) {
      setNameError(true);
      setNameErrorMessage('Name is required.');
      isValid = false;
    } else {
      setNameError(false);
      setNameErrorMessage('');
    }

    return isValid;
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    if (validateInputs()) {
      const email = document.getElementById('email').value;
      const password = document.getElementById('password').value;
      const name = document.getElementById('name').value;

      try {
        const response = await axios.post('http://localhost:5000/auth/sign-up', { email, password, name });
        console.log('Sign-up success:', response.data);
        navigate('/sign-in');
      } catch (error) {
        console.error('Error during sign-up:', error.response?.data?.error || error.message);
      }
    }
  };

  const handleSignInRedirect = () => {
    navigate('/sign-in');
  };

  return (
    <div style={{ padding: '20px', marginTop: '10vh', color: 'white' }}>
      <div style={{ width: '462px', height: '600px', margin: 'auto', padding: '20px', backgroundImage: `url(${Diary})`, backgroundPosition: 'center', backgroundSize: 'cover' }}>
        <h1 style={{ fontSize: '2rem', textAlign: 'center', marginTop: '40px' }}>Sign up</h1>
        <form onSubmit={handleSubmit} noValidate style={{ display: 'flex', flexDirection: 'column', gap: '16px', marginLeft: '20px' }}>
          <div style={{ display: 'flex', flexDirection: 'column' }}>
            <label htmlFor="name">Full name</label>
            <input
              type="text"
              id="name"
              name="name"
              placeholder="Jon Snow"
              required
              style={{ padding: '8px', borderColor: nameError ? 'red' : '#ccc' }}
            />
            {nameError && <span style={{ color: 'red' }}>{nameErrorMessage}</span>}
          </div>
          <div style={{ display: 'flex', flexDirection: 'column' }}>
            <label htmlFor="email">Email</label>
            <input
              type="email"
              id="email"
              name="email"
              placeholder="your@email.com"
              required
              style={{ padding: '8px', borderColor: emailError ? 'red' : '#ccc' }}
            />
            {emailError && <span style={{ color: 'red' }}>{emailErrorMessage}</span>}
          </div>
          <div style={{ display: 'flex', flexDirection: 'column' }}>
            <label htmlFor="password">Password</label>
            <input
              type="password"
              id="password"
              name="password"
              placeholder="••••••"
              required
              style={{ padding: '8px', borderColor: passwordError ? 'red' : '#ccc' }}
            />
            {passwordError && <span style={{ color: 'red' }}>{passwordErrorMessage}</span>}
          </div>
          <button
            type="submit"
            style={{ padding: '10px', backgroundColor: 'skyblue', color: 'black', border: 'none', cursor: 'pointer' }}
          >
            Sign up
          </button>
          <p style={{ textAlign: 'center' }}>
            Already have an account?{' '}
            <a href='/sign-in'
              onClick={handleSignInRedirect} style={{ color: 'skyblue' }}>
              Sign in
            </a>
          </p>
        </form>
      </div>
    </div>
  );
}
