import React, { useState } from 'react';

function About() {
  const [hoveredIndex, setHoveredIndex] = useState(null);
  const [isFlipping, setIsFlipping] = useState(Array(8).fill(false));

  const handleMouseEnter = (index) => {
    setHoveredIndex(index);
    const newFlippingState = [...isFlipping];
    newFlippingState[index] = true;
    setIsFlipping(newFlippingState);
  };

  const handleMouseLeave = (index) => {
    setHoveredIndex(null);
    setTimeout(() => {
      const newFlippingState = [...isFlipping];
      newFlippingState[index] = false;
      setIsFlipping(newFlippingState);
    }, 1000); // Duration of the flip transition
  };

  return (
    <div>
      <header>
        <h2>LifeLens</h2>
        <nav style={{
          position: 'fixed',
          top: 0,
          left: 0,
          width: '100%',
          height: '80px',
          display: 'flex',
          justifyContent: 'space-between',
          alignItems: 'center',
          backgroundColor: 'black',
          boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
          zIndex: 1000
        }}>
          <div style={lifelens}>LifeLens</div>
          <div style={{ display: 'flex', justifyContent: 'flex-end' }}>
            {/* Navigation buttons omitted for brevity */}
          </div>
        </nav>
      </header>

      <div style={{
        display: 'grid',
        gridTemplateColumns: 'repeat(4, 1fr)',
        gap: '20px',
        justifyItems: 'center',
        padding: '100px 20px 20px'
      }}>
        {[...Array(8)].map((_, index) => (
          <div
            key={index}
            style={{
              backgroundColor: 'transparent',
              width: '320px',
              height: '254px',
              perspective: '1000px',
              fontFamily: 'sans-serif',
            }}
          >
            <div style={{
              position: 'relative',
              width: '100%',
              height: '100%',
              textAlign: 'center',
              transition: 'transform 1s', // Slow transition for smooth flipping
              transformStyle: 'preserve-3d',
              transform: isFlipping[index] ? 'rotateY(180deg)' : 'rotateY(0deg)',
            }}
              onMouseEnter={() => handleMouseEnter(index)}
              onMouseLeave={() => handleMouseLeave(index)}
            >
              {/* Front Side */}
              <div style={{
                boxShadow: '0 8px 14px 0 rgba(0,0,0,0.2)',
                position: 'absolute',
                display: 'flex',
                flexDirection: 'column',
                justifyContent: 'center',
                width: '100%',
                height: '100%',
                backfaceVisibility: 'hidden',
                border: '1px solid coral',
                borderRadius: '1rem',
                background: 'linear-gradient(120deg, bisque 60%, rgb(255, 231, 222) 88%, rgb(255, 211, 195) 40%, rgba(255, 127, 80, 0.603) 48%)',
                color: 'coral'
              }}>
                <p style={{ fontSize: '1.5em', fontWeight: '900', textAlign: 'center', margin: 0 }}>FLIP CARD</p>
                <p>Hover Me</p>
              </div>
              
              {/* Back Side */}
              <div style={{
                boxShadow: '0 8px 14px 0 rgba(0,0,0,0.2)',
                position: 'absolute',
                display: 'flex',
                flexDirection: 'column',
                justifyContent: 'center',
                width: '100%',
                height: '100%',
                backfaceVisibility: 'hidden',
                border: '1px solid coral',
                borderRadius: '1rem',
                background: 'linear-gradient(120deg, rgb(255, 174, 145) 30%, coral 88%, bisque 40%, rgb(255, 185, 160) 78%)',
                color: 'white',
                transform: 'rotateY(180deg)'
              }}>
                <p style={{ fontSize: '1.5em', fontWeight: '900', textAlign: 'center', margin: 0 }}>BACK</p>
                <p>Leave Me</p>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default About;

const lifelens = {
  color: '#644dff',
  width: '184px',
  height: '45px',
  display: 'flex',
  alignItems: 'center',
  justifyContent: 'center',
  gap: '1rem',
  fontSize: '2em',
  fontWeight: '800',
  letterSpacing: '2px',
  marginLeft: '20px'
};
