import React from 'react';
import homeImg from '../assets/homeimg.png';
import { Link } from 'react-router-dom';

export default function Landing() {
  const headerStyle = {
    width: '100%',
    padding: '15px 100px',
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    backgroundColor: '#171717',
    color: '#ffffff',
    position: 'fixed',
  };

  const logoStyle = {
    fontSize: '24px',
    fontWeight: 'bold',
  };

  const navStyle = {
    display: 'flex',
  };

  const navLinkStyle = {
    color: '#ffffff',
    textDecoration: 'none',
    marginLeft: '20px',
  };

  const homeSectionStyle = {
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    padding: '50px 100px',
    backgroundColor: '#171717',
  };

  const homeContentStyle = {
    display: 'flex',
    width: '100%',
    color: 'rgb(255, 255, 255)',
    backgroundColor: '#171717',
    borderRadius: '20px',
    marginTop: '60px',
  };

  const textStyle = {
    width: '60%',
    paddingLeft: '50px',
    marginTop: '42.5px',
    textAlign: 'left',
    paddingTop: '30px',
    color: '#fff',
    fontWeight: 70,
  };

  const imageStyle = {
    width: '40%',
  };

  const imgStyle = {
    width: '100%',
    height: 'auto',
  };

  const aboutSectionStyle = {
    padding: '50px 100px',
    backgroundColor: '#171717',
    color: '#fff',
    fontWeight: 70,
    width: '90%',
  };

  const servicesSectionStyle = {
    padding: '50px 100px',
    backgroundColor: '#171717',
  };

  const landingcardContainerStyle = {
    display: 'flex',
    flexWrap: 'wrap',
    justifyContent: 'center',
    maxWidth: '1200px',
    margin: '50px auto',
    padding: '20px',
  };

  const landingcardStyle = {
    flexDirection: 'column',
    flex: '1 1 300px',
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    padding: '20px',
    margin: '10px',
    borderRadius: '10px',
    boxShadow: '0px 5px 10px rgba(0, 0, 0, 0.1)',
    transition: 'transform 0.3s ease',
    backgroundColor: '#31a8da',
    color: '#000000',
  };

  const landingcardHoverStyle = {
    transform: 'translateY(-5px)',
    boxShadow: '0px 10px 20px rgba(0, 0, 0, 0.2)',
  };

  const landingcardHeaderStyle = {
    marginBottom: '10px',
  };

  const landingcardParaStyle = {
    fontSize: '16px',
  };

  const footerStyle = {
    width: '100%',
    padding: '20px 0',
    backgroundColor: '#171717',
    color: '#fff',
    textAlign: 'center',
  };

  return (
    <div className='landing'>
      <header style={headerStyle}>
        <h2 className="logo" style={logoStyle}>Resolve Radar</h2>
        <nav className='navigation' style={navStyle}>
          <a href="/" style={navLinkStyle}>Home</a>
          <a href="/#about" style={navLinkStyle}>About</a>
          <a href="/#services" style={navLinkStyle}>Services</a>
          <Link style={{...navLinkStyle, color: '#31a8da'}} to='/users'>Login/SignUp</Link>
        </nav>
      </header>
      <section id="home" className="home-section" style={homeSectionStyle}>
        <div className="home-content" style={homeContentStyle}>
          <div className="text" style={textStyle}>
            <h1 style={{color:'#31a8da'}}>Welcome to our Student Complaint Management System!</h1>
            <p style={{ fontSize: '20px', marginTop:'20px' }}> &emsp; Our platform is dedicated to ensuring that every student's voice is heard and every issue is addressed promptly. Whether it's a problem with facilities, academic concerns, or any other issue, we're here to help you get the resolution you need.</p>
            <ul>
              <li style={{ fontSize: '20px' }}>Easy complaint registration process</li>
              <li style={{ fontSize: '20px' }}>Real-time tracking of complaint status</li>
              <li style={{ fontSize: '20px' }}>Notifications for updates on your complaints</li>
              <li style={{ fontSize: '20px' }}>Dedicated problem solvers to ensure timely resolutions</li>
            </ul>
          </div>
          <div className="image" style={imageStyle}>
            <img src={homeImg} alt="Home" style={imgStyle}/>
          </div>
        </div>
      </section>
      <section id="about" className="about-section" style={aboutSectionStyle}>
        <h2 style={{color:'#31a8da'}}>About Us</h2>
        <div className='underline' style={{width: '50%', height: '6px', background: '#31a8da', borderRadius: '9px', margin: '20px'}}></div>
        <p>We believe in fostering a transparent, efficient, and user-friendly environment for addressing and resolving complaints. Our Online Complaint and Management System is designed to streamline the process of lodging complaints, tracking their status, and ensuring timely resolutions. Whether you are a student, employee, or member of the administration, our platform is here to serve your needs efficiently and effectively.</p>
        <p>Our mission is to provide a reliable and accessible platform where all stakeholders can voice their concerns and complaints without any hassle. We are committed to ensuring that every issue is addressed promptly and fairly, contributing to a positive and productive environment for everyone.</p>
      </section>
      <section id="services" className="services-section" style={servicesSectionStyle}>
        <h2 style={{color:'#31a8da'}}>Our Services</h2>
        <div className='underline1' style={{width: '50%', height: '6px', background: '#31a8da', borderRadius: '9px'}}></div>
        <div className="landingcard-container" style={landingcardContainerStyle}>
          <div className="landingcard" style={landingcardStyle}>
            <h3 style={landingcardHeaderStyle}>Complaint Registration</h3>
            <p style={landingcardParaStyle}>Easily register your complaints through our user-friendly platform. Provide all necessary details to help us understand and address your issue better.</p>
          </div>
          <div className="landingcard" style={landingcardStyle}>
            <h3 style={landingcardHeaderStyle}>Real-Time Tracking</h3>
            <p style={landingcardParaStyle}>Keep track of your complaint's status in real-time. Receive notifications and updates as your complaint moves through the resolution process.</p>
          </div>
          <div className="landingcard" style={landingcardStyle}>
            <h3 style={landingcardHeaderStyle}>Feedback and Improvements</h3>
            <p style={landingcardParaStyle}>Your feedback is valuable to us. After your complaint is resolved, provide feedback to help us improve our services and make the system even better for future users.</p>
          </div>
        </div>
      </section>
      <footer style={footerStyle}>
        <p>&copy; 2024 Student Complaint Management System. All rights reserved.</p>
      </footer>
    </div>
  );
}
