import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';  // Ensure you have react-router-dom installed and set up
import Calendar from "react-calendar";
import "react-calendar/dist/Calendar.css";
import "./CalendarPage.css";

const CalendarPage = () => {
    const [selectedDate, setSelectedDate] = useState(null);  // State to hold selected date
    const navigate = useNavigate();  // Hook to navigate programmatically

    // Function to handle date click
    const Date_Click_Fun = (date) => {
        setSelectedDate(date);
    };

    // Function to navigate to add-data page with selected date
    const handleAddData = () => {
        if (selectedDate) {
            navigate(`/add-data/${selectedDate.toDateString()}`);  // Navigates with the selected date
        }
    };

    // Function to navigate to the view-data page
    const handleViewData = () => {
        navigate("/view-data");
    };

    return (
        <div className="calendar-page">
            <h1>Select a Date from the Calendar</h1>
            <div className="calendar-container">
                <Calendar 
                    onClickDay={Date_Click_Fun}
                    value={selectedDate}  // Show selected date on the calendar
                />
            </div>
            {/* Show buttons only if a date is selected */}
            {selectedDate && (
                <div className="button-group">
                    <button onClick={handleAddData}>Add Data</button>
                    <button onClick={handleViewData}>View Data</button>
                </div>
            )}
        </div>
    );
};

export default CalendarPage;
