import React, { useState } from 'react';

const AddDataPage = ({ selectedDate, handleUpload, setCurrentPage }) => {
    const [data, setData] = useState(''); // State to hold form input

    // Handle input change
    const handleChange = (event) => {
        setData(event.target.value);
    };

    // Handle form submission
    const handleSubmit = (event) => {
        event.preventDefault();
        if (selectedDate) {
            handleUpload(selectedDate, data); // Call the handleUpload function passed via props
            setCurrentPage('calendar'); // Navigate back to the calendar page
        } else {
            alert('Please select a date before adding data.');
        }
    };

    return (
        <div style={{ padding: '20px', marginTop: '10vh', width: '80%', maxWidth: '600px', margin: 'auto' }}>
            <h1>Add Data</h1>
            <form onSubmit={handleSubmit}>
                <div style={{ marginBottom: '20px' }}>
                    <label htmlFor="dataInput" style={{ display: 'block', marginBottom: '10px' }}>
                        Data for {selectedDate ? selectedDate.toDateString() : 'Selected Date'}
                    </label>
                    <textarea
                        id="dataInput"
                        value={data}
                        onChange={handleChange}
                        rows="5"
                        style={{
                            width: '100%',
                            padding: '10px',
                            borderRadius: '5px',
                            border: '1px solid #ccc',
                            boxSizing: 'border-box'
                        }}
                    />
                </div>
                <button type="submit" style={{ padding: '10px 20px', borderRadius: '5px', border: 'none', backgroundColor: '#4CAF50', color: '#fff', cursor: 'pointer' }}>
                    Submit
                </button>
                <button
                    type="button"
                    onClick={() => setCurrentPage('calendar')}
                    style={{ padding: '10px 20px', borderRadius: '5px', border: 'none', backgroundColor: '#f44336', color: '#fff', cursor: 'pointer', marginLeft: '10px' }}
                >
                    Cancel
                </button>
            </form>
        </div>
    );
};

export default AddDataPage;
